function filterSongs(){var input,filter,ul,li,div,i,txtValue;input=document.getElementById('searchBox');filter=input.value.toUpperCase();ul=document.getElementById("songList");li=ul.getElementsByTagName('li');for(i=0;i<li.length;i++){song=li[i].getElementsByTagName("div")[0];artist=li[i].getElementsByTagName("div")[1];songTxt=song.textContent||song.innerText;artistTxt=artist.textContent||artist.innerText;if(songTxt.toUpperCase().indexOf(filter)>-1||artistTxt.toUpperCase().indexOf(filter)>-1){li[i].style.display="";}else{li[i].style.display="none";}}}
function openInNewTab(url){var win=window.open(url,'_blank');win.focus();}
document.addEventListener("DOMContentLoaded",function(){var audio=document.getElementById("audio");var playButton=document.getElementById("playButton");var pauseButton=document.getElementById("pauseButton");var stopButton=document.getElementById("stopButton");var progressBar=document.getElementById("progressBar");var progress=document.getElementById("progress");var volumeSlider=document.getElementById("volumeSlider")
volumeSlider.value=localStorage.getItem('volume')||'30';audio.volume=volumeSlider.value / 100;playButton.addEventListener("click",function(){pauseButton.style.display='block';stopButton.style.display='block';audio.play();});pauseButton.addEventListener("click",function(){audio.pause();});stopButton.addEventListener("click",function(){audio.pause();audio.currentTime=0;});audio.addEventListener("timeupdate",function(){var percentage=(audio.currentTime / audio.duration)*100;progress.style.width=percentage+"%";});progressBar.addEventListener("click",function(event){var rect=progressBar.getBoundingClientRect();var offsetX=event.clientX-rect.left;var percentage=offsetX / progressBar.offsetWidth;audio.currentTime=percentage*audio.duration;});volumeSlider.addEventListener("input",function(){audio.volume=volumeSlider.value / 100;localStorage.setItem('volume',volumeSlider.value);});});function toggleTheme(){const currentTheme=document.documentElement.getAttribute('data-theme');const newTheme=(currentTheme==='dark')?'light':'dark';document.documentElement.setAttribute('data-theme',newTheme);localStorage.setItem('theme',newTheme);}
document.addEventListener('DOMContentLoaded',function(){const savedTheme=localStorage.getItem('theme')||'light';document.documentElement.setAttribute('data-theme',savedTheme);});let touchstartX=0;let touchendX=0;document.addEventListener('touchstart',function(event){touchstartX=event.changedTouches[0].screenX;},false);document.addEventListener('touchend',function(event){touchendX=event.changedTouches[0].screenX;handleGesture();},false);function handleGesture(){if(touchendX+500<touchstartX){window.history.forward()}
if(touchendX-500>touchstartX){window.history.back()}}
function toggleDropdown(){var content=document.getElementById("dropdownContent");content.classList.toggle("show");}
window.onclick=function(event){if(!event.target.matches('#drop-down-button')){var dropdowns=document.getElementsByClassName("dropdown-content");for(var i=0;i<dropdowns.length;i++){var openDropdown=dropdowns[i];if(openDropdown.classList.contains("show")){openDropdown.classList.remove("show");}}}}